package softwarecoidng_02;

public class ex18 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int kopo37_year = 0;
		int kopo37_age = 14;
		
		System.out.println(kopo37_year);
		System.out.println(kopo37_age);
		
		kopo37_year = kopo37_age + 2000; // age 변수 +2000을 더해 year 변수에 저장
		kopo37_age = kopo37_age + 1; // age 변수 1 증가
		
		System.out.println(kopo37_year);
		System.out.println(kopo37_age);
		
		
	}

}
