package softwarecoidng_02;

public class ex21 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println('\''); // 
		System.out.println("abc\t123\b456"); // \b 는 백스페이스 역할로 3이 지워짐
		System.out.println('\n'); // 개행 역할 수행
		System.out.println("\"Hello\""); //   \"  \"  큰따옴표를 출력하기 위해 \" 를 써야함
		System.out.println("c:\\"); //  위와 마찬가지로 System.out.println("c:\"); 이렇게 하면 에러가 남
	//	System.out.println("c:\");
	}

}
