package softwarecoidng_02;

public class ex28 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float kopo37_f = 9.1234567f; // float형 변수
		double kopo37_d = 9.1234567; // double 변수
		double kopo37_d2 = (double)kopo37_f; // float를 double으로 형변환
		
		System.out.printf("f=%20.18f\n", kopo37_f); // float
		System.out.printf("d=%20.18f\n", kopo37_d); // double
		System.out.printf("d2=%20.18f\n", kopo37_d2); // float ==> double
	}

}
